                                                                                                                      clc;
close all;
clear all;
a=imread('qq.tif');

f=fft2(a);
f1=abs(f);

f2=uint8(ifft2(f));
figure,
subplot(1,3,1);imshow(a);
subplot(1,3,2);imshow(f1,[]);
subplot(1,3,3);imshow(f2,[]);

fs=fftshift(f);
fs1=abs(fs);
figure,imshow(fs);

ifs=ifftshift(fs);
fr=uint8(ifft2(ifs));

figure,
subplot(1,3,1);imshow(a);
subplot(1,3,2);imshow(fs1,[]);
subplot(1,3,3);imshow(fr,[]);

s=log(1+fs1);
figure,imshow(s,[]);
